    private void fToCActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_fToCActionPerformed
        //Store number in string
        String numb1String = Fbox.getText();
        //Convert the string into a number
        double numb1 = Integer.parseInt(numb1String);
        //Converting f to c
        double converted = (numb1 - 32) * 0.5556;
        //Putting answer in box
        Fbox.setText("" + converted);

    }//GEN-LAST:event_fToCActionPerformed

