    private void cToFActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cToFActionPerformed
        //Store number in string
        String numb2String = Cbox.getText();
        //Convert the string into a number
        double numb2 = Integer.parseInt(numb2String);
        //Converting f to c
        double converted2 = numb2 * 1.8 + 32;
        //Putting answer in box
        Cbox.setText("" + converted2);
    }//GEN-LAST:event_cToFActionPerformed

